/*
 *
 * Determines the new operator for a filter based on the current operator, old and new filter values.
 *
 * This handles cases where the filter values have transitioned from a single value to multiple values (or vice versa),
 * and the current operator needs to be transitioned to its plural form (or singular form).
 *
 * For example, if the current operator is 'is', and the new filter values have a length of 2, the
 * new operator would be 'is any of'.
 *
 */
export function determineNewOperator<TType extends ColumnDataType>(
  type: TType,
  oldVals: FilterValues<TType>,
  nextVals: FilterValues<TType>,
  currentOperator: FilterOperators[TType],
  locale: Locale,
): FilterOperators[TType] {
  const a =
    Array.isArray(oldVals) && Array.isArray(oldVals[0])
      ? oldVals[0].length
      : oldVals.length
  const b =
    Array.isArray(nextVals) && Array.isArray(nextVals[0])
      ? nextVals[0].length
      : nextVals.length

  // If filter size has not transitioned from single to multiple (or vice versa)
  // or is unchanged, return the current operator.
  if (a === b || (a >= 2 && b >= 2) || (a <= 1 && b <= 1))
    return currentOperator

  const opDetails = getFilterTypeOperatorDetails(locale)[type][currentOperator]

  // Handle transition from single to multiple filter values.
  if (a < b && b >= 2) return opDetails.singularOf ?? currentOperator
  // Handle transition from multiple to single filter values.
  if (a > b && b <= 1) return opDetails.pluralOf ?? currentOperator
  return currentOperator
}

'use client'

import { useMemo, useState } from 'react'
import { createColumns } from '../core/filters'
import { DEFAULT_OPERATORS, determineNewOperator } from '../core/operators'
import type {
  ColumnConfig,
  ColumnDataType,
  ColumnOption,
  DataTableFilterActions,
  FilterModel,
  FilterStrategy,
  FiltersState,
  OptionBasedColumnDataType,
  OptionColumnIds,
} from '../core/types'
import { uniq } from '../lib/array'
import { addUniq, removeUniq } from '../lib/array'
import {
  createNumberFilterValue,
  isColumnOptionArray,
  isColumnOptionMap,
} from '../lib/helpers'
import { Locale } from '../lib/i18n'

export interface DataTableFiltersOptions<
  TData,
  TColumns extends ReadonlyArray<ColumnConfig<TData, any, any, any>>,
  TStrategy extends FilterStrategy,
> {
  strategy: TStrategy
  data: TData[]
  columnsConfig: TColumns
  controlledState?:
  | [FiltersState, React.Dispatch<React.SetStateAction<FiltersState>>]
  | undefined
  options?: Partial<
    Record<
      OptionColumnIds<TColumns>,
      | ColumnOption[]
      | [ColumnOption[] | undefined, Map<string, number> | undefined]
    >
  >
  locale?: Locale
}

export function useDataTableFilters<
  TData,
  TColumns extends ReadonlyArray<ColumnConfig<TData, any, any, any>>,
  TStrategy extends FilterStrategy,
>({
  strategy,
  data,
  columnsConfig,
  controlledState,
  options,
  locale = 'en',
}: DataTableFiltersOptions<TData, TColumns, TStrategy>) {
  const [internalFilters, setInternalFilters] = useState<FiltersState>([])
  const [filters, setFilters] = controlledState ?? [
    internalFilters,
    setInternalFilters,
  ]

  // Convert ColumnConfig to Column, applying options and faceted options if provided
  const columns = useMemo(() => {
    const enhancedConfigs = columnsConfig.map((config) => {
      if (
        options &&
        (config.type === 'option' || config.type === 'multiOption')
      ) {
        const optionsInput = options[config.id as OptionColumnIds<TColumns>]

        if (!optionsInput) return config

        if (isColumnOptionArray(optionsInput)) {
          return { ...config, options: optionsInput }
        }

        if (
          isColumnOptionArray(optionsInput[0]) &&
          isColumnOptionMap(optionsInput[1])
        ) {
          return {
            ...config,
            options: optionsInput[0],
            facetedOptions: optionsInput[1],
          }
        }
      }

      return config
    })
    return createColumns(data, enhancedConfigs, strategy)
  }, [data, columnsConfig, options, strategy])

  const actions: DataTableFilterActions = useMemo(
    () => ({
      addFilterValue<TData, TType extends OptionBasedColumnDataType>(
        column: ColumnConfig<TData, TType>,
        values: FilterModel<TType>['values'],
      ) {
        if (column.type === 'option') {
          setFilters((prev) => {
            const filter = prev.find((f) => f.columnId === column.id)
            const isColumnFiltered = filter && filter.values.length > 0
            if (!isColumnFiltered) {
              return [
                ...prev,
                {
